<div  class="row"><div  class="col-xl-9 col-lg-12"><div  class="card"><form  id="cookies-setting"  method="post"  action="<?php echo Router::url(); ?>adminv3/system/update_cookies/"><div  class="row"><div  class="col-md-3 static-text"></div> <div  class="col-md-9"><label><input  type="radio"  name="cookie-accept-show"  value='1' <?php if((is_bool(  $model['cookieAcceptShow'] == 1 ) && (  $model['cookieAcceptShow'] == 1 )) || !is_bool(  $model['cookieAcceptShow'] == 1 )) { echo " checked=\"" . ($model['cookieAcceptShow'] == 1) . "\""; } ?> /> <?php echo t("Show the bar with the consent of cookies, and must be accepted"); ?></label><br /><label><input  type="radio"  name="cookie-accept-show"  value='2' <?php if((is_bool(  $model['cookieAcceptShow'] == 2 ) && (  $model['cookieAcceptShow'] == 2 )) || !is_bool(  $model['cookieAcceptShow'] == 2 )) { echo " checked=\"" . ($model['cookieAcceptShow'] == 2) . "\""; } ?> /> <?php echo t("Display a panel with information about cookies"); ?></label><br /><label><input  type="radio"  name="cookie-accept-show"  value='0' <?php if((is_bool(  $model['cookieAcceptShow'] == 0 ) && (  $model['cookieAcceptShow'] == 0 )) || !is_bool(  $model['cookieAcceptShow'] == 0 )) { echo " checked=\"" . ($model['cookieAcceptShow'] == 0) . "\""; } ?> /> <?php echo t("Don't show the cookie bar"); ?></label></div> </div>  <?php if($model['cookieAcceptShow'] != 0) { ?><div  class="row"><div  class="col-md-3 static-text"><?php echo t("Text for cookie"); ?></div> <div  class="col-md-9"><textarea  rows="2"  name="cookie-text"><?php echo $model["cookieText"]; ?></textarea> </div> </div>  <?php if($model['cookieAcceptShow'] == 1) { ?><div  class="row"><div  class="col-md-3 static-text"><?php echo t("Accept button text"); ?></div> <div  class="col-md-9"><input  type="text" <?php if((is_bool(  $model['cookieAccept'] ) && (  $model['cookieAccept'] )) || !is_bool(  $model['cookieAccept'] )) { echo " value=\"" . ($model['cookieAccept']) . "\""; } ?>  name="accept-text" /></div> </div>  <?php } ?><div  class="row"><div  class="col-md-3 static-text"><?php echo t("More button link"); ?></div> <div  class="col-md-9"><input  type="text" <?php if((is_bool(  $model['cookieMore'] ) && (  $model['cookieMore'] )) || !is_bool(  $model['cookieMore'] )) { echo " value=\"" . ($model['cookieMore']) . "\""; } ?>  name="cookie-more" /></div> </div>  <?php if($model['cookieAcceptShow'] == 1) { ?><div  class="row"><div  class="col-md-3 -static-text"><?php echo t("Javascript code that will be executed without consent"); ?></div> <div  class="col-md-9"><textarea  rows="3"  name="cookie-no-js"><?php echo $model["cookieNoJs"]; ?></textarea> </div> </div>  <?php } ?><div  class="row"><div  class="col-md-3 -static-text"><?php echo t("Javascript code that will be executed with consent"); ?></div> <div  class="col-md-9"><textarea  rows="3"  name="cookie-js"><?php echo $model["cookieJs"]; ?></textarea> </div> </div> <div  class="row"><div  class="col-md-3 -static-text"><?php echo t("The name of cookies what will be created after accept"); ?></div> <div  class="col-md-9"><input  type="text"  value="cookieAccept"  readonly /></div> </div>  <?php } ?><div  class="row"><div  class="col-md-3"></div> <div  class="col-md-9"><button  type="submit"  class="submit button button-primary"  name="save"><?php echo t('Save'); ?></button>  </div> </div> </form> </div> </div> <div  class="col-xl-3 col-lg-12"><div  class="card"><div  class="card-title"><?php echo t("Found cookies"); ?></div> <div  class="card-content"><ul  class="card-list"> <?php foreach($model["cookiesList"] as $id => $cookie) { ?><li><div  class="right-text"><?php echo Strings::str_time($cookie["created"]); ?></div> <span  class="material-symbols-outlined small">cookie</span>  <b><?php echo $cookie["name"]; ?></b><div  class="small"><i><?php echo $cookie["location"]; ?></i></div> </li> <?php } ?></ul> </div>             </div> </div> </div> <script> $("[name=cookie-accept-show]").on("change", function(){
        manager.submitForm($("#cookies-setting").find("[type=submit]"));
    });
