<?php foreach($model["rows"] as $key => $row) { ?><tr><td>#<?php echo $row["id"]; ?></td><td> <?php if($row["isSystemPermission"]) { ?><span  class="material-symbols-outlined"  title="<?php echo t('System permission'); ?>">security</span>  <?php } ?></td><td><?php echo $row["action"]; ?></td><td> <?php if($row["isBlockByIp"]) { ?> <?php echo $row["block"]; ?> <?php } else { ?> <?php echo $row["user"]["nick"]; ?> <?php } ?></td><td> <?php if($row["blocked_by"]["user"] == null) { ?><i  title="<?php echo t('This action was performed unloged'); ?>"><?php echo t("Missing"); ?></i>   / <?php echo $row["blocked_by"]["ip"]; ?> <?php } else { ?> <?php echo $row["blocked_by"]["user"]["nick"]; ?> / <?php echo $row["blocked_by"]["ip"]; ?> <?php } ?></td><td><?php echo Strings::str_time($row["expires"]); ?></td><td> <?php echo $row["information"]; ?> <?php if($row["internal_information"] != "") { ?><i>(<?php echo $row["internal_information"]; ?>)</i> <?php } ?></td><td> <?php if(!$row["isExpired"]) { ?><a  href="#"  onclick="deleteBlocking(<?php echo $row['id']; ?>);return false;"  class="button"><span  class="material-symbols-outlined"  title="<?php echo t('Delete'); ?>">delete</span> </a>  <?php } else { ?><i><?php echo t("Expired"); ?></i> <?php } ?></td></tr> <?php } ?><script> var total_rows = <?php echo $model['total']; ?>;
    var page = <?php echo $model['page']; ?>;
    table_blocks.pushInfo({total: total_rows, page: page });
</script>