<?php foreach($model["rows"] as $key => $row) { ?><tr><td> #<?php echo $row["id"]; ?></td><td> <?php if($row["isDeleted"]) { ?><span  class="material-symbols-outlined"  title="<?php echo t('Deleted'); ?>">delete</span>  <?php } ?></td><td> <?php echo Strings::str_time($row["date"]); ?></td><td> <?php echo $row["text"]; ?></td><td> <?php if($row["isCustom"]) { ?><span  class="material-symbols-outlined"  title="<?php echo t('Not logged in'); ?>">remove_moderator</span>  <i><?php echo $row["author"]["nick"]; ?></i> <?php } else { ?> <?php echo $row["author"]["nick"]; ?> <?php } ?></td><td> <?php echo $row["ip"]; ?></td><td> <?php if($row["isDeleted"]) { ?><a  href="#"  class="button"  onclick="restoreComments(<?php echo $row['id']; ?>);"><span  class="material-symbols-outlined">recycling</span>   <?php echo t("Restore"); ?></a>   <?php } else { ?><a  href="#"  class="button"  onclick="deleteComments(<?php echo $row['id']; ?>);"><span  class="material-symbols-outlined">delete</span>   <?php echo t("Delete"); ?></a>   <?php } ?> <?php if($row["parentType"] == "article") { ?><a  href="<?php echo Router::url(); ?><?php echo $row['parentData']; ?>"  class="button"><span  class="material-symbols-outlined"  title="<?php echo t('View'); ?>">visibility</span> </a> <?php } ?></td></tr> <?php } ?><script> var total_rows = <?php echo $model['total']; ?>;
    var page = <?php echo $model['page']; ?>;
    table_comments.pushInfo({total: total_rows, page: page });
</script>