<?php foreach($model["rows"] as $key => $row) { ?><tr><td>#<?php echo $row["id"]; ?></td><td> <?php if($row["isSystemPermission"]) { ?><span  class="material-symbols-outlined"  title="<?php echo t('System permission'); ?>">security</span>  <?php } ?></td><td><?php echo $row["name"]; ?></td><td><?php echo $row["count"]; ?></td><td><?php echo $row["level"]; ?></td><td> <?php $style = $row["color"] == "black"? "color:white;": "";; ?><span  class="badge"  style="--badge-color: <?php echo $row['color']; ?>;<?php echo $style; ?>"><?php echo $row["color"]; ?></span> </td><td><a  href="<?php echo Router::url(); ?>adminv3/users/edit_permission/<?php echo $row['id']; ?>"  class="button"><span  class="material-symbols-outlined">edit</span>   <?php echo t("Edit"); ?></a>   <?php if($row["isSystemPermission"]) { ?><a  href="#"  class="button disabled"  disabled="disabled"  onclick="return false;"><span  class="material-symbols-outlined"  title="<?php echo t('Permission cannot be deleted'); ?>">lock</span> </a>  <?php } else { ?><a  href="#"  class="button"  onclick="deletePermission('<?php echo $row["id"]; ?>');return false;"><span  class="material-symbols-outlined"  title="<?php echo t('Delete permission'); ?>">delete</span> </a>  <?php } ?></td></tr> <?php } ?><script> var total_rows = <?php echo $model['total']; ?>;
    var page = <?php echo $model['page']; ?>;
    table_permission.pushInfo({total: total_rows, page: page });
</script>